const initialState = {
  todoItems: [
    {
      id: 1,
      description: 'Have a cup of tea',
      isDone: false,
      important: false
    },
    {
      id: 2,
      description: 'Have a cup of coffee',
      isDone: false,
      important: false
    },
    {
      id: 3,
      description: 'Have a cup of juice',
      isDone: false,
      important: false
    },
    { id: 4, description: 'Eat some fruit', isDone: false, important: false },
    { id: 5, description: 'Write some code', isDone: false, important: false }
  ]
};

const appReducer = (state = initialState, action) => {
  switch (action.type) {
    case 'DELETE_TODO_ITEM': {
      const todoItems = state.todoItems.filter(
        item => item.id !== action.payload
      );
      return { ...state, todoItems };
    }
    case 'MARK_TODO_ITEM_IMPORTANT': {
      const todoItems = state.todoItems.map(item => {
        item;
      });
    }
    default:
      return state;
  }
};

export default appReducer;
