import { generateID } from '../utils';

const initialState = {
  todoItems: [
    {
      id: generateID(),
      description: 'Have a cup of tea',
      isDone: false,
      important: false,
      done: true
    },
    {
      id: generateID(),
      description: 'Have a cup of coffee',
      isDone: false,
      important: false,
      done: false
    },
    {
      id: generateID(),
      description: 'Have a cup of juice',
      isDone: false,
      important: false,
      done: false
    },
    {
      id: generateID(),
      description: 'Eat some fruit',
      isDone: false,
      important: false,
      done: false
    },
    {
      id: generateID(),
      description: 'Write some code',
      isDone: false,
      important: false,
      done: false
    }
  ],
  statusFilter: 'active'
};

const appReducer = (state = initialState, action) => {
  switch (action.type) {
    case 'DELETE_TODO_ITEM': {
      const todoItems = state.todoItems.filter(
        item => item.id !== action.payload
      );
      return { ...state, todoItems };
    }
    case 'MARK_TODO_ITEM_IMPORTANT': {
      const todoItems = state.todoItems.map(item => ({ ...item }));
      const targetItem = todoItems.find(item => item.id === action.payload);
      targetItem.important = !targetItem.important;
      return { ...state, todoItems };
    }
    case 'MARK_TODO_ITEM_DONE': {
      const todoItems = state.todoItems.map(item => ({ ...item }));
      const targetItem = todoItems.find(item => item.id === action.payload);
      targetItem.done = !targetItem.done;
      return { ...state, todoItems };
    }
    case 'ADD_TODO_ITEM': {
      const todoItems = state.todoItems.map(item => ({ ...item }));
      todoItems.push(action.payload);
      return { ...state, todoItems };
    }
    case 'FILTER_BY_STATUS': {
      return { ...state, statusFilter: action.payload };
    }
    default:
      return state;
  }
};

export default appReducer;
