import React from 'react';
import { connect } from 'react-redux';

import {
  deleteTodoItem,
  markTodoItemImportant,
  markTodoItemDone
} from '../../actions/todo-list-actions';
import TodoListItem from '../todo-list-item';

const TodoList = ({
  todoItems,
  statusFilter,
  searchFilter,
  deleteTodoItem,
  markTodoItemImportant,
  markTodoItemDone
}) => {
  const filterByStatus = (items, status) => {
    if (status === 'done') return items.filter(item => item.done);
    if (status === 'active') return items.filter(item => !item.done);
    return items;
  };

  const filterBySearch = (items, value) => {
    return items.filter(item => item.description.includes(value));
  };

  const createTodos = items =>
    items.map(item => (
      <li key={item.id} className="list-group-item">
        <TodoListItem
          deleteTodoItem={deleteTodoItem}
          markTodoItemImportant={markTodoItemImportant}
          markTodoItemDone={markTodoItemDone}
          {...item}
        />
      </li>
    ));

  const itemsByStatus = filterByStatus(todoItems, statusFilter);
  console.log(itemsByStatus);
  const itemsBySearch = filterBySearch(itemsByStatus, searchFilter);
  console.log(itemsBySearch);
  const todos = createTodos(itemsBySearch);

  return <ul className="list-group">{todos}</ul>;
};

const mapStateToProps = state => {
  return {
    todoItems: state.todoListReducer.todoItems,
    statusFilter: state.todoListReducer.statusFilter,
    searchFilter: state.todoListReducer.searchFilter
  };
};

const mapDispatchToProps = dispatch => {
  return {
    deleteTodoItem: itemID => dispatch(deleteTodoItem(itemID)),
    markTodoItemImportant: itemID => dispatch(markTodoItemImportant(itemID)),
    markTodoItemDone: itemID => dispatch(markTodoItemDone(itemID))
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(TodoList);
