import React from 'react';
import { connect } from 'react-redux';

import { deleteTodoItem } from '../../actions/todoListActions';
import TodoListItem from '../todo-list-item';

const TodoList = ({ todoItems, deleteTodoItem }) => {
  const todos = todoItems.map(item => (
    <li
      key={item.id}
      className="list-group-item"
      deleteTodoItem={deleteTodoItem}
    >
      <TodoListItem {...item} />
    </li>
  ));

  return <ul className="list-group">{todos}</ul>;
};

const mapStateToProps = state => {
  return {
    todoItems: state.appReducer.todoItems
  };
};

const mapDispatchToProps = dispatch => {
  return {
    deleteTodoItem: itemID => dispatch(deleteTodoItem(itemID))
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(TodoList);
