import React, { Component } from 'react';
import { connect } from 'react-redux';

import { generateID } from '../../utils';
import { addTodoItem } from '../../actions/todo-list-actions';

class AddItemPanel extends Component {
  state = {
    inputValue: ''
  };

  onInputChange = event => {
    console.log(event.target.value);
  };

  createTodoItem = description => ({
    id: generateID(),
    description,
    isDone: false,
    important: false,
    done: false
  });

  submitItem = event => {
    event.preventDefault();
    const input = document.getElementById('new-item-input');
    const description = input.value;
    if (description.length >= 3) {
      const item = this.createTodoItem(input.value);
      this.props.addTodoItem(item);
      input.value = '';
    }
  };

  render() {
    return (
      <form onSubmit={event => this.submitItem(event)} className="mt-3">
        <div className="form-row">
          <input
            className="form-control col mr-1 pl-2"
            id="new-item-input"
            type="text"
            placeholder="New task here..."
          />
          <button type="submit" className="btn btn-outline-secondary">
            Add Task
          </button>
        </div>
      </form>
    );
  }
}

const mapStateToProps = state => {
  return {};
};

const mapDispatchToProps = dispatch => {
  return {
    addTodoItem: itemID => dispatch(addTodoItem(itemID))
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(AddItemPanel);
