import React from 'react';
import { connect } from 'react-redux';

import { addTodoItem } from '../../actions/add-item-panel-actions';

const AddItemPanel = ({ addTodoItem }) => {
  const createTodoItem = description => 
    ({ id: 1, description, isDone: false, important: false, done: false });

  const submitItem = event => {
    event.preventDefault();
    const newItemDescription = event.target.getElementsByClassName('new-item-name')[0].value
    const newItem = createTodoItem(newItemDescription);
    addTodoItem(newItem);
  }

  return (
    <form onSubmit={(event) => submitItem(event)} className="mt-3">
      <div className="form-row">
        <input className="form-control new-item-name col mr-1 pl-2" type="text" placeholder="task name here..." />
        <button type="submit" className="btn btn-outline-secondary">Add Task</button>
      </div>
    </form>
  );
};

const mapStateToProps = state => {
  return {};
}

const mapDispatchToProps = dispatch => {
  return {
    addTodoItem: item => dispatch(addTodoItem(item))
  }
}

export default AddItemPanel;
