const addItemPanelReducer = (state, action) => {
  switch (action.type) {
    case 'ADD_TODO_ITEM': {
      console.log(state);
      const todoItems = state.todoItems.map(item => ({...item}));
      todoItems.push(action.payload);
      return { ...state, todoItems};
    }
    default:
      return state;
  }
}

export default addItemPanelReducer;
