import React from 'react';

const AddItemPanel = () => {
  return (
    <form>
      <div className="form-row">
        <input className="form-control col-8" type="text" placeholder="New task..." />
        <button className="btn btn-outline-secondary col">Add Task</button>
      </div>
    </form>
  );
};

export default AddItemPanel;
