const initialState = {
  todoItems: [
    { id: 1, description: 'Have a cup of tea', isDone: false },
    { id: 2, description: 'Have a cup of coffee', isDone: false },
    { id: 3, description: 'Have a cup of juice', isDone: false }
  ]
};

const appReducer = (state = initialState, action) => {
  switch (action.type) {
    default:
      return state;
  }
};

export default appReducer;
