import React from 'react';
import { connect } from 'react-redux';

import { deleteTodoItem, markTodoItemImportant } from '../../actions/todoListActions';
import TodoListItem from '../todo-list-item';

const TodoList = ({ todoItems, deleteTodoItem, markTodoItemImportant }) => {
  const todos = todoItems.map(item => (
    <li key={item.id} className="list-group-item">
      <TodoListItem deleteTodoItem={deleteTodoItem} markTodoItemImportant={markTodoItemImportant} {...item} />
    </li>
  ));

  return <ul className="list-group">{todos}</ul>;
};

const mapStateToProps = state => {
  return {
    todoItems: state.todoListReducer.todoItems
  };
};

const mapDispatchToProps = dispatch => {
  return {
    deleteTodoItem: itemID => dispatch(deleteTodoItem(itemID)),
    markTodoItemImportant: itemID => dispatch(markTodoItemImportant(itemID))
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(TodoList);
