import React from 'react';
import { connect } from 'react-redux';

const StatusFilter = ({ buttons }) => {
  const buttonsJSX = buttons.map(btn => (
    <button key={btn.label} className="btn btn-outline-secondary">
      {btn.label}
    </button>
  ));
  return <div className="btn-group">{buttonsJSX}</div>;
};

const mapStateToProps = state => {
  return {
    buttons: state.todoListReducer.statusFilterButtons
  };
};

export default connect(mapStateToProps)(StatusFilter);
